package com.entity;

public class Banji {
	private int cid;
    private int num;
	public Banji() {
		super();
		// TODO Auto-generated constructor stub
	}
	public Banji(int cid, int num) {
		super();
		this.cid = cid;
		this.num = num;
	}
	public int getCid() {
		return cid;
	}
	public void setCid(int cid) {
		this.cid = cid;
	}
	public int getNum() {
		return num;
	}
	public void setNum(int num) {
		this.num = num;
	}
    
}
